import React from "react";
import "./DescriptionBox.css";

const DescriptionBox = () => {
  return (
    <div className="descriptionbox">
      <div className="descriptionbox-navigator">
        <div className="descriptionbox-nav-box">About US</div>
      </div>
      <div className="descriptionbox-description">
        <p>
        The Loom Company is a forward-thinking apparel brand dedicated to crafting high-quality, minimalistic clothing that seamlessly blends contemporary style with timeless elegance. Our mission is to empower individuals through fashion that embodies strength, resilience, and a distinct aesthetic.
        </p>
        <p>
        At The Loom Company, we invite you to join us in celebrating the power of minimalistic design and the strength of the human spirit. Our clothing is more than just fashion; it's a statement of who you are and what you stand for. Explore our collections and find the perfect pieces that resonate with your inner strength and style.
        </p>
      </div>
    </div>
  );
};

export default DescriptionBox;
